// Storage utilities for Snap Doc Pro
export class StorageManager {
  static async get(keys) {
    return new Promise((resolve) => {
      chrome.storage.session.get(keys, resolve);
    });
  }

  static async set(data) {
    return new Promise((resolve) => {
      chrome.storage.session.set(data, resolve);
    });
  }

  static async remove(keys) {
    return new Promise((resolve) => {
      chrome.storage.session.remove(keys, resolve);
    });
  }

  static async clear() {
    return new Promise((resolve) => {
      chrome.storage.session.clear(resolve);
    });
  }

  // Local storage for settings
  static async getSettings() {
    return new Promise((resolve) => {
      chrome.storage.local.get(['snapDocSettings'], (result) => {
        resolve(result.snapDocSettings || this.getDefaultSettings());
      });
    });
  }

  static async saveSettings(settings) {
    return new Promise((resolve) => {
      chrome.storage.local.set({ snapDocSettings: settings }, resolve);
    });
  }

  static getDefaultSettings() {
    return {
      theme: 'light',
      defaultImageSize: 'medium',
      autoSave: true,
      showNotifications: true,
      defaultExportFormat: 'docx',
      includeTimestamps: true,
      includePageInfo: true,
      compressionQuality: 90,
      maxScreenshots: 50
    };
  }
}

// Export utilities
export class ExportManager {
  constructor() {
    this.formats = {
      docx: this.exportDocx.bind(this),
      pdf: this.exportPdf.bind(this),
      html: this.exportHtml.bind(this),
      zip: this.exportZip.bind(this)
    };
  }

  async export(snapshots, format, options) {
    if (!this.formats[format]) {
      throw new Error(`Unsupported export format: ${format}`);
    }

    return await this.formats[format](snapshots, options);
  }

  async exportDocx(snapshots, options) {
    const { title, imageSize, includeTimestamps, includePageInfo } = options;

    const imageSizes = {
      small: { width: 300, height: 200 },
      medium: { width: 500, height: 300 },
      large: { width: 700, height: 450 }
    };
    const { width, height } = imageSizes[imageSize] || imageSizes.medium;

    const children = [
      new docx.Paragraph({
        children: [
          new docx.TextRun({
            text: title,
            bold: true,
            size: 32
          })
        ]
      }),
      new docx.Paragraph({
        children: [
          new docx.TextRun({
            text: `Generated on: ${new Date().toLocaleString()}`,
            italics: true,
            size: 20
          })
        ]
      }),
      new docx.Paragraph({ children: [new docx.TextRun({ text: "" })] })
    ];

    for (let idx = 0; idx < snapshots.length; idx++) {
      const snapshot = snapshots[idx];

      try {
        // Convert image
        const response = await fetch(snapshot.dataUrl);
        const blob = await response.blob();
        const arrayBuffer = await blob.arrayBuffer();
        const imageBuffer = new Uint8Array(arrayBuffer);

        // Add title
        children.push(
          new docx.Paragraph({
            children: [
              new docx.TextRun({
                text: `Screenshot ${idx + 1}`,
                bold: true,
                size: 24
              })
            ]
          })
        );

        // Add page info if available and enabled
        if (includePageInfo && snapshot.url) {
          children.push(
            new docx.Paragraph({
              children: [
                new docx.TextRun({
                  text: `URL: ${snapshot.url}`,
                  size: 18,
                  color: "666666"
                })
              ]
            })
          );
        }

        // Add timestamp if enabled
        if (includeTimestamps && snapshot.timestamp) {
          children.push(
            new docx.Paragraph({
              children: [
                new docx.TextRun({
                  text: `Captured: ${snapshot.timestamp}`,
                  size: 18,
                  color: "666666"
                })
              ]
            })
          );
        }

        // Add annotation
        if (snapshot.annotation?.trim()) {
          children.push(
            new docx.Paragraph({
              children: [
                new docx.TextRun({
                  text: `Note: ${snapshot.annotation.trim()}`,
                  italics: true,
                  size: 20
                })
              ]
            })
          );
        }

        children.push(
          new docx.Paragraph({ children: [new docx.TextRun({ text: "" })] })
        );

        // Add image
        children.push(
          new docx.Paragraph({
            children: [
              new docx.ImageRun({
                data: imageBuffer,
                transformation: { width, height }
              })
            ]
          })
        );

        children.push(
          new docx.Paragraph({ children: [new docx.TextRun({ text: "" })] })
        );

      } catch (error) {
        console.error(`Error processing image ${idx + 1}:`, error);
        children.push(
          new docx.Paragraph({
            children: [
              new docx.TextRun({
                text: `[Error loading Screenshot ${idx + 1}: ${error.message}]`,
                italics: true
              })
            ]
          })
        );
      }
    }

    const doc = new docx.Document({
      creator: "Snap Doc Pro",
      title: title,
      description: "Screenshots captured with Snap Doc Pro",
      sections: [{ children }]
    });

    return await docx.Packer.toBlob(doc);
  }

  async exportHtml(snapshots, options) {
    const { title, includeTimestamps, includePageInfo } = options;

    let html = `
      <!DOCTYPE html>
      <html>
      <head>
        <title>${title}</title>
        <style>
          body { font-family: system-ui, sans-serif; max-width: 800px; margin: 0 auto; padding: 20px; }
          .header { text-align: center; margin-bottom: 30px; }
          .screenshot { margin-bottom: 30px; page-break-inside: avoid; }
          .screenshot h3 { color: #333; }
          .screenshot img { max-width: 100%; height: auto; border: 1px solid #ddd; }
          .meta { color: #666; font-size: 0.9em; margin: 10px 0; }
          .annotation { background: #f5f5f5; padding: 10px; border-left: 4px solid #007cba; margin: 10px 0; }
          @media print { .screenshot { page-break-after: always; } }
        </style>
      </head>
      <body>
        <div class="header">
          <h1>${title}</h1>
          <p>Generated on: ${new Date().toLocaleString()}</p>
        </div>
    `;

    snapshots.forEach((snapshot, idx) => {
      html += `<div class="screenshot">`;
      html += `<h3>Screenshot ${idx + 1}</h3>`;

      if (includePageInfo && snapshot.url) {
        html += `<div class="meta">URL: <a href="${snapshot.url}">${snapshot.url}</a></div>`;
      }

      if (includeTimestamps && snapshot.timestamp) {
        html += `<div class="meta">Captured: ${snapshot.timestamp}</div>`;
      }

      if (snapshot.annotation?.trim()) {
        html += `<div class="annotation">${snapshot.annotation}</div>`;
      }

      html += `<img src="${snapshot.dataUrl}" alt="Screenshot ${idx + 1}">`;
      html += `</div>`;
    });

    html += `</body></html>`;

    return new Blob([html], { type: 'text/html' });
  }

  async exportZip(snapshots, options) {
    // This would require a ZIP library like JSZip
    // For now, return a placeholder
    const content = JSON.stringify({ snapshots, options, exported: new Date().toISOString() }, null, 2);
    return new Blob([content], { type: 'application/json' });
  }

  async exportPdf(snapshots, options) {
    // This would require a PDF library like jsPDF
    // For now, return HTML that can be printed to PDF
    return this.exportHtml(snapshots, options);
  }
}

// Notification utilities
export class NotificationManager {
  static show(title, message, type = 'success') {
    // Create in-app notification
    const notification = document.createElement('div');
    notification.className = `snap-notification snap-${type}`;
    notification.innerHTML = `
      <div class="snap-notification-content">
        <strong>${title}</strong>
        <p>${message}</p>
      </div>
    `;

    document.body.appendChild(notification);

    setTimeout(() => {
      notification.classList.add('show');
    }, 10);

    setTimeout(() => {
      notification.classList.remove('show');
      setTimeout(() => notification.remove(), 300);
    }, 3000);
  }

  static error(title, message) {
    this.show(title, message, 'error');
  }

  static success(title, message) {
    this.show(title, message, 'success');
  }

  static info(title, message) {
    this.show(title, message, 'info');
  }
}
